import "./styles.css";


import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


import { Row, Col } from "react-flexa";

import Product from "./Product";
import { Component } from "react";
import { render } from "react-dom";


import qs from "qs"
import Filters from "./filter";
import NavBar from "./Navbar";
import { ThemeConsumer } from "styled-components";

class Products extends Component {
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = { test:1,counter: 0, isLoading :true, Products_data:{} };
    this.handleClick = this.handleClick.bind(this);
    this.setState({
      test: this.props.toggle

    })
    alert(this.props.toggle)
  }

  handleClick = () => {};

  componentDidMount() {

    let url = location.search;
    console.log("url", url)

    fetch(
      "http://localhost:3000/shoes"+url
    ).then((data) => {
      return data.json()

    })
    .then((data)=>{

      this.setState({
        Products_data : data,
        isLoading :false,

      })

    })
  }
  render(){
    var {isLoading, Products_data} = this.state
  return (
    <div className="App">
      <Row style={{ justifyContent: "" }}>
      {isLoading ? ('Loading'):
            (Object.keys(Products_data).map((pro)=>{
              return (
                <Col xs={12} sm={12} md={6} lg={3} alignself="auto">
                <Product data={Products_data[pro]} />

              </Col>

              )
            }))


            }


      </Row>

      </div>
  );
  }
}

class Home extends Component {
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = { test:'',counter: 0, isLoading :true, Products_data:{} };
    this.handleClick = this.handleClick.bind(this);

  }

  handleClick = () => {
    this.setState({
      test: location.search
    })




  };


  render() {
    var {isLoading,test, Products_data} = this.state

    return (
      <div className="App">

        <NavBar />
        <div className="body">
          <div className="Filter_container">
            <div className="filter">


                <Filters toggle={this.handleClick} />


            </div>
          </div>

          <div className="Products_container">
            <h3 style={{ marginLeft: 20 }}> {console.log(this.state.test)} SHOES</h3>

             <Products toggle={this.state.test} />

          </div>
        </div>
          </div>
    );
  }
}



export default function App(){
  return(
    <Router>
    <Route path="/shoe"  component={Home} />

      </Router>

  )

}
