import "./styles.css";
import {Component} from 'react'
import qs from "qs"

export default class Filters extends Component{
    constructor(props){
        super(props);
        this.state = {
            color:'',
            type:'',
            result:'',
            isLoading:true

        
        }
        let url = location.search;
        qs.parse(url, ignoreQueryPrefix=true).then((result)=>{
            this.setState({
                data : result,
                isLoading:false
            })
        })
    }

    _handleclick=(e)=>{
        let temp = e.target.className;
        this.setState({
            temp : e.target.value 
        })
        
    }
    check=(e)=>{
        let temp = e.target.value
        if (temp == this.state.data.temp)
        return true

    }
    
    render(){

    return(

        <div>
            <div class="filter_title">
            Filters
            
            </div>
            <form onSubmit={(e)=>{e.preventDefault()}}>
                
                
                <br />
                <label for="cost" style={{fontSize:20}}>Cost <br /></label>
                <input type="checkbox" id="vehicle1" name="cost" value="Bike" />
                <label for="vehicle1"> I have a bike</label><br /> 
                <input type="checkbox" id="vehicle2" name="cost" value="Car" />
                <label for="vehicle2"> I have a car</label><br />
                <input type="checkbox" id="vehicle3" name="cost" value="Boat" />
                <label for="vehicle3"> I have a boat</label><br /><br />
                
                
                <label for="cost"  style={{fontSize:20}} >Type <br /></label>
                <input type="checkbox" id="type1" name="type" value="Sneakers" />
                <label for="vehicle1"> Sneakers</label><br /> 
                <input type="checkbox" id="type2" name="type" value="Sliders" />
                <label for="vehicle2"> Sliders</label><br />
                <input type="checkbox" id="type3" name="type" value="Chappals" />
                <label for="vehicle3"> Chappals</label><br /><br />

                <label for="color" style={{fontSize:20}} >Color <br /></label>
                            
                <input onChange={(e)=>{this._handleclick(e)}} type="checkbox" class="color" id="vehicle1" name="color" value="red" checked={this.check(e)} />
                <label for="Red"> Red</label><br /> 
                <input onChange={(e)=>{this._handleclick(e)}} type="checkbox" class="color" id="vehicle2" name="color" value="green" />
                <label for="Green"> Green</label><br />
                <input onChange={(e)=>{this._handleclick(e)}} type="checkbox" class="color" id="vehicle3" name="color" value="blue" />
                <label for="Blue"> Blue</label><br /><br />

                <button type="submit"> Apply</button>
                </form> 
            </div>

    )

    }
}
