      </Row>

      <h1>Hello CodeSandbox</h1>
      <h2>Start editing to see some magic happen!</h2>
    </div>
  );
  }
}

export default class App extends Component {
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = { counter: 0, isLoading :true, Products_data:{} };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick = () => {};
  componentDidMount() {
    fetch(
      "http://localhost:3000/shoes?color=blue&type=Sneakers&sort_price=-1"
    ).then((data) => {
      return data.json()

    })
    .then((data)=>{

      this.setState({
        Products_data : data,
        isLoading :false,

      })
      console.log(data)
    })
  }
  render() {
    var {isLoading, Products_data} = this.state
    console.log(this.state.products_data)
    return (
      <div className="App">
        <div className="body">
          <div className="Filter_container">
            <div className="filter">
              <h3> Filter</h3>
              <form>
                <label>Price</label>
                <br />
                <label>Brand</label>
              </form>
            </div>
          </div>

          <div className="Products_container">
            <h3 style={{ marginLeft: 20 }}> SHOES</h3>
            {isLoading ? ('s'):
            (Object.keys(Products_data).map((pro)=>{
              return <Product />
            }))


            }
             <Product />
            {!isLoading && console.log(Products_data)}
          </div>
        </div>
        <h1>Hello CodeSandbox</h1>
        <h2>Start editing to see some magic happen!</h2>
      </div>
    );
  }
}
