import "./styles.css";

import "./styles.css";

import { Row, Col } from "react-flexa";

import Product from "./Product";
import { Component } from "react";
import { render } from "react-dom";

class Products extends Component {
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = { counter: 0, isLoading :true, Products_data:{} };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick = () => {};
  componentDidMount() {
    fetch(
      "http://localhost:3000/shoes?color=blue"
    ).then((data) => {
      return data.json()

    })
    .then((data)=>{

      this.setState({
        Products_data : data,
        isLoading :false,

      })

    })
  }
  render(){
    var {isLoading, Products_data} = this.state
  return (
    <div className="App">
      <Row style={{ justifyContent: "" }}>
      {isLoading ? ('s'):
            (Object.keys(Products_data).map((pro)=>{
              return (
                <Col xs={12} sm={12} md={6} lg={3} alignself="auto">
                <Product data={pro} />
              </Col>

              )
            }))


            }


      </Row>

      <h1>Hello CodeSandbox</h1>
      <h2>Start editing to see some magic happen!</h2>
    </div>
  );
  }
}

export default class App extends Component {
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = { counter: 0, isLoading :true, Products_data:{} };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick = () => {};
  componentDidMount() {
    fetch(
      "http://localhost:3000/shoes?color=blue&type=Sneakers&sort_price=-1"
    ).then((data) => {
      return data.json()

    })
    .then((data)=>{

      this.setState({
        Products_data : data,
        isLoading :false,

      })

    })
  }
  render() {
    var {isLoading, Products_data} = this.state

    return (
      <div className="App">
        <div className="body">
          <div className="Filter_container">
            <div className="filter">
              <h3> Filter</h3>
              <form>
                <label>Price</label>
                <br />
                <label>Brand</label>
              </form>
            </div>
          </div>

          <div className="Products_container">
            <h3 style={{ marginLeft: 20 }}> SHOES</h3>

             <Products />

          </div>
        </div>
        <h1>Hello CodeSandbox</h1>
        <h2>Start editing to see some magic happen!</h2>
      </div>
    );
  }
}
