import "./styles.css";


import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";


import { Row, Col } from "react-flexa";

import Product from "./Product";
import { Component } from "react";
import { render } from "react-dom";


import qs from "qs"
import Filters from "./filter";
import NavBar from "./Navbar";
import { ThemeConsumer } from "styled-components";
import Products from "./Products";
import Cart from "./Cart";
import Products2 from "./Products_2";
import Filters2 from "./filter2";



class Home extends Component {
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = { test:'',counter: 0, isLoading :true, Products_data:{} };
    this.handleClick = this.handleClick.bind(this);

  }

  handleClick = () => {
    this.setState({
      test: location.search
    })




  };


  render() {
    var {isLoading,test, Products_data} = this.state

    return (
      <div className="App">

        <NavBar />
        <div className="body">
          <div className="Filter_container">
            <div className="filter">


                <Filters toggle={this.handleClick} />


            </div>
          </div>

          <div className="Products_container">
            <div className="product_header">
            <h3 style={{ marginLeft: 20 }}> {console.log(this.state.test)} SHOES</h3>
            <div style={{marginLeft:'auto'}}>ss</div>

              </div>
              <Products toggle={test} />

          </div>
        </div>
          </div>
    );
  }
}




class Home2 extends Component {
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = { test:'',counter: 0, isLoading :true, Products_data:{} };
    this.handleClick = this.handleClick.bind(this);

  }

  handleClick = (e) => {

  console.log(e)






  };


  render() {
    var {isLoading,test, Products_data} = this.state

    return (
      <div className="App">

        <NavBar />
        <div className="body">
          <div className="Filter_container">
            <div className="filter">


                <Filters2 toggle={this.handleClick} />


            </div>
          </div>

          <div className="Products_container">
            <div className="product_header">
            <h3 style={{ marginLeft: 20 }}> {console.log(this.state.test)} SHOES</h3>
            <div style={{marginLeft:'auto'}}>
              <form>
                <input name="sort_price" value={1} />
                <input type="submit" onClick={this.handleClick} value="post" />
                </form>
            </div>

              </div>
              <Products2 />

          </div>
        </div>
          </div>
    );
  }
}



export default function App(){
  return(
    <Router>
    <Route path="/shoe"  component={Home} />
    <Route path="/cart"  component={Cart} />
    <Route path="/shoe2" component={Home2} />

      </Router>

  )

}
